/* Procedure structure for procedure `aumentar_intentos` */

/*!50003 DROP PROCEDURE IF EXISTS  `aumentar_intentos` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`tvpuenet`@`localhost` PROCEDURE `aumentar_intentos`(IN `correoo` VARCHAR(45))
BEGIN
	UPDATE tbl_usuario 
	SET 
		estado = CASE
			WHEN intentos=3 THEN 2
			ELSE 1
		END,
		intentos = CASE
			WHEN estado=2 THEN 0
			ELSE intentos+1
		END
	WHERE `correo` = correoo;
	SELECT intentos-1
	FROM tbl_usuario
	WHERE `correo` = correoo AND
	`estado`=1;
END */$$
DELIMITER ;

