/*!50003 CREATE DEFINER=`tvpuenet`@`localhost` PROCEDURE `buscar_usuario`(IN `cadena` VARCHAR(50))
BEGIN
	SELECT 
		id_usuario,
		nombre,
		no_trabajador,
		tipoU.tipo_usuario AS "Tipo",
		estado
	FROM 
		(SELECT * FROM cat_tipo_usuario) AS tipoU,
		`tbl_usuario`		
	WHERE tbl_usuario.id_tipo_usuario=tipoU.id_tipo_usuario
	AND (tbl_usuario.nombre LIKE CONCAT('%', cadena, '%')
	OR tbl_usuario.no_trabajador LIKE CONCAT('%', cadena, '%'))
	;
END */$$
